# Kirara Encoder AviSynth Channel Script
function Stereo2KE(clip KEIPA) { SPKL = GetChannel(KEIPA, %FL%) SPKR = GetChannel(KEIPA, %FR%) Return MergeChannels(SPKL, SPKR) }
function Stereo6KE(clip KEIPA) { SPKL = GetChannel(KEIPA, %FL%) SPKR = GetChannel(KEIPA, %FR%) SPKC = GetChannel(KEIPA, %FC%) SPKLFE = GetChannel(KEIPA, %LFE%) SPKBL = GetChannel(KEIPA, %RL%) SPKBR = GetChannel(KEIPA, %RR%) SPKMixL = MixAudio(SPKL, SPKC, 1, 0.7071) SPKMixL2 = MixAudio(SPKR, SPKBL, 0, 1) SPKMixL3 = MixAudio(SPKBR, SPKLFE, 0, 0.7071) SPKMixL4 = MixAudio(SPKMixL, SPKMixL2) SPKMixL5 = MixAudio(SPKMixL3, SPKMixL4) SPKMixR = MixAudio(SPKL, SPKC, 0, 0.7071) SPKMixR2 = MixAudio(SPKR, SPKBL, 1, 0) SPKMixR3 = MixAudio(SPKBR, SPKLFE, 1, 0.7071) SPKMixR4 = MixAudio(SPKMixR, SPKMixR2) SPKMixR5 = MixAudio(SPKMixR3, SPKMixR4) Return MergeChannels(SPKMixL5, SPKMixR5) }
function LFE516KE(clip KEIPA) { SPKL = GetChannel(KEIPA, %FL%) SPKR = GetChannel(KEIPA, %FR%) SPKC = GetChannel(KEIPA, %FC%) SPKLFE = GetChannel(KEIPA, %LFE%) SPKBL = GetChannel(KEIPA, %RL%) SPKBR = GetChannel(KEIPA, %RR%) Return MergeChannels(SPKL, SPKR, SPKC, SPKLFE, SPKBL, SPKBR) }
function DolbySurroundProLogic6KE(clip KEIPA) { SPKL = GetChannel(KEIPA, %FL%) SPKR = GetChannel(KEIPA, %FR%) SPKC = GetChannel(KEIPA, %FC%) SPKLFE = GetChannel(KEIPA, %LFE%) SPKBL = GetChannel(KEIPA, %RL%) SPKBR = GetChannel(KEIPA, %RR%) SPKMixL = MixAudio(SPKL, SPKC, 1, 0.7071) SPKMixL2 = MixAudio(SPKR, SPKBL, 0, -1) SPKMixL3 = MixAudio(SPKBR, SPKLFE, -1, 0.7071) SPKMixL4 = MixAudio(SPKMixL, SPKMixL2) SPKMixL5 = MixAudio(SPKMixL3, SPKMixL4) SPKMixR = MixAudio(SPKL, SPKC, 0, 0.7071) SPKMixR2 = MixAudio(SPKR, SPKBL, 1, 1) SPKMixR3 = MixAudio(SPKBR, SPKLFE, 1, 0.7071) SPKMixR4 = MixAudio(SPKMixR, SPKMixR2) SPKMixR5 = MixAudio(SPKMixR3, SPKMixR4) Return MergeChannels(SPKMixL5, SPKMixR5) }
function DolbyProLogicII6KE(clip KEIPA) { SPKL = GetChannel(KEIPA, %FL%) SPKR = GetChannel(KEIPA, %FR%) SPKC = GetChannel(KEIPA, %FC%) SPKLFE = GetChannel(KEIPA, %LFE%) SPKBL = GetChannel(KEIPA, %RL%) SPKBR = GetChannel(KEIPA, %RR%) SPKMixL = MixAudio(SPKL, SPKC, 1, 0.7071) SPKMixL2 = MixAudio(SPKR, SPKBL, 0, -0.866) SPKMixL3 = MixAudio(SPKBR, SPKLFE, -0.5, 0.7071) SPKMixL4 = MixAudio(SPKMixL, SPKMixL2) SPKMixL5 = MixAudio(SPKMixL3, SPKMixL4) SPKMixR = MixAudio(SPKL, SPKC, 0, 0.7071) SPKMixR2 = MixAudio(SPKR, SPKBL, 1, 0.5) SPKMixR3 = MixAudio(SPKBR, SPKLFE, 0.866, 0.7071) SPKMixR4 = MixAudio(SPKMixR, SPKMixR2) SPKMixR5 = MixAudio(SPKMixR3, SPKMixR4) Return MergeChannels(SPKMixL5, SPKMixR5) }
